import { useState, useEffect } from 'react';
import './Products.css';
import {Card, CardText, CardBody, CardTitle, CardImg} from 'reactstrap';
import Button from 'react-bootstrap/Button';


const Products = () => {

    const [articles, setArticles] = useState([]);

    const fetchArticles = async () => {

        const response = await fetch("http://localhost:8000/articles", {
          method: "GET",
          mode: "cors",
        });
        const parseResponse = await response.json();
        setArticles(parseResponse);
      };

      useEffect(() => {
        fetchArticles();
      }, []);

      return (
        <div className="fond">
          <h1 align="center">Articles</h1>
          <div className="grid">
           <div className="container entete">
             <h2 className="sous-titre">Pick one and let's travel !</h2>
           </div>
           <div className="container univers">
             <h2 className="sous-titre">Pick one and let's travel !</h2>
           </div>
          </div>
         
         
          <div className="list-group">
            {articles.map((article) => {
              return (
                <div className="list-group-item" key={article.articleId}>
                  {article.name}
                  <p>{article.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      );
};

export default Products;
