import { useState, useEffect } from 'react';
import './Products.css';
import Card from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';

const Products = () => {

    const [articles, setArticles] = useState([]);

    const fetchArticles = async () => {

        const response = await fetch("http://localhost:8000/articles", {
          method: "GET",
          mode: "cors",
        });
        const parseResponse = await response.json();
        setArticles(parseResponse);
      };

      useEffect(() => {
        fetchArticles();
      }, []);

      return (
        <div className="fond">
          <h1 align="center">Articles</h1>
          <div className="grid">
           <div className="container entete">
             <h2 className="sous-titre">Pick one and let's travel !</h2>
           </div>
          </div>
          <Container>
  <Row>
    <Col xs={6} md={4}>
      <Image src="holder.js/171x180" rounded />
    </Col>
    <Col xs={6} md={4}>
      <Image src="holder.js/171x180" roundedCircle />
    </Col>
    <Col xs={6} md={4}>
      <Image src="holder.js/171x180" thumbnail />
    </Col>
  </Row>
</Container>
          
         
          <div className="list-group">
            {articles.map((article) => {
              return (
                <div className="list-group-item" key={article.articleId}>
                  {article.name}
                  <p>{article.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      
      );
};

export default Products;
